#2 triangular random variates
mytriang <- function(n,a,b,c){
  u <- runif(seq(n))
  x <- c()
  for (i in u){
    if(0<i && i <((c-a)/(b-a))){
      x<- append(x,a+sqrt((i)*(b-a)*(c-a)))
    } else if (((c-a)/(b-a))<=i && i<1){
      x<-append(x,b-sqrt((1-i)*(b-a)*(b-c)))
    }

  }
  hist(x,freq=FALSE)
  segments(a,0,c,2/(b-a))
  segments(c,2/(b-a),b,0)
  return (x)
}
      
a <- 10
b <- 25
c <- 15
n <- 10000

#first sample
triangle <- mytriang(n,a,b,c)
mean(triangle)
sd(triangle)

